import React, {useState} from "react";
import InputMask from "react-input-mask";
import { useForm, Controller } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup"
import * as yup from "yup"
import axios from "axios"


const schema = yup
    .object({
        email: yup.string().required(),
        number: yup.number().positive().integer().transform((value) => (isNaN(value) ? undefined : value)),
    })


const formatNumber = value => {
    return value.replace(/\s/g, "").match(/.{1,3}/g).join("-") || ""
}
// const formatNumber = value => console.log("value", value)

function App() {

    const [data, setData] = useState([])

    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm({
        resolver: yupResolver(schema),
    })

    const onSubmit = (data) => {

        console.log("data", data)

        // axios.post('/search', data)
        //     .then(res => setData(res.data))
        //     .catch(function (error) {
        //         console.log(error);
        //     });
    }

    const { onChange, onBlur, name, ref } = register('firstName');

    return (

      <main>
          <form onSubmit={handleSubmit(onSubmit)}>

              <input placeholder='email' {...register("email" )} />
              <p>{errors.email?.message}</p>

              {/*<input*/}
              {/*    placeholder='999 999'*/}
              {/*    type="tel"*/}
              {/*    {...register("number", {*/}
              {/*        onChange: (e) => {*/}
              {/*            const {value} = e.target*/}
              {/*            e.target.value = formatNumber(value)*/}
              {/*        }*/}
              {/*    } )}*/}
              {/*/>*/}


              <InputMask name="number" mask="999-999">
                  {(inputProps) => (
                      <input
                          {...inputProps}
                          ref={register}
                          type="text"
                          placeholder="Input NPWP"
                      />
                  )}
              </InputMask>


              <button type="submit">submit</button>
          </form>

          {data?.map((user, i) => <p key={i}>{user.email} {user.number}</p>)}
      </main>

  );
}

export default App;
